script.script:
import renderD2 from "https://cdn.jsdelivr.net/npm/@d2lang/d2js@0.6.4/+esm";

document.addEventListener("DOMContentLoaded", async () => {
  const source = `
    direction: right
    App -> API
    API -> DB
  `;

  const svg = await renderD2(source);
  document.getElementById("diagram").innerHTML = svg;
});
