svg.script:
function renderAllD2Blocks() {
  const codeBlocks = Array.from(document.querySelectorAll('pre > code.language-d2'));

  codeBlocks.forEach((code) => {
    const source = code.textContent;
    const container = document.createElement('div');
    container.style.border = '1px solid #ccc';
    container.style.margin = '1em 0';
    container.style.overflow = 'auto';

    const pre = code.parentElement;
    pre.parentElement.replaceWith(container);

    D2.render({ src: source, container });
  });
}

function waitUntilReady() {
  if (typeof D2 !== 'undefined' && document.readyState === 'complete') {
    renderAllD2Blocks();
  } else {
    setTimeout(waitUntilReady, 100);
  }
}

waitUntilReady();
